import { useEffect, useState } from "react";
import Logo from "../assets/logo.png";
import "../App.css";

export const Header = () => {
  const [theme, setTheme] = useState("gOne");

  useEffect(() => {
    document.documentElement.classList.add(theme);
  }, [theme]);

  return (
    <header>
      <div className="logo">
        <img src={Logo} alt="Taskmate Logo" />
        <span>Taskmate</span>
      </div>
      <div className="themeSelector">
        <span onClick={() => setTheme("light")} className="light"></span>
        <span onClick={() => setTheme("light")} className="medium"></span>
        <span onClick={() => setTheme("light")} className="dark"></span>
        <span onClick={() => setTheme("light")} className="gOne"></span>
        <span onClick={() => setTheme("light")} className="gTwo"></span>
        <span className="gThree"></span>
      </div>
    </header>
  );
};
